package first;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		while(i<=15)
		{
			System.out.println("Hello "+i);
			i++;
		}
		
		System.out.println("Reverse prining ");
		i=15;
		while(i>=1)
		{
			System.out.println("Hello "+i);
			i--;
		}
		
	}

}
